from collections import defaultdict

def draw_map(records):
    graphs = []
    for record in records:
        graph = defaultdict(list)
        rooms = {}
        current_room = 1
        current_distance = 0
        for num in record.split():
            if num == '0':
                break
            if int(num) > 0:
                num_of_doors = int(num)
                for _ in range(num_of_doors):
                    graph[current_room].append(current_distance + 1)
                current_distance += 1
                rooms[current_room] = current_distance
                current_room += 1
            else:
                num = int(num)
                graph[current_room].append(rooms[current_distance + num])
        for i in range(1, current_room):
            graph[i].sort()
            graph[i] = list(set(graph[i]))
        graphs.append(graph)
    return graphs

def print_graph(graph):
    for i in range(1, len(graph) + 1):
        print(f'{i} {" ".join(map(str, graph[i]))} {len(graph[i])}')

def main():
    n = int(input())
    records = []
    for _ in range(n):
        record = input()
        while record and record[-1] != '0':
            record += input()
        records.append(record)
    for graph in draw_map(records):
        print_graph(graph)

if __name__ == "__main__":
    main()
