#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

void shredder(int target, string num) {
    vector<int> parts;
    int sum = 0;
    for (char c : num) {
        int part = c - '0';
        if (sum + part <= target) {
            sum += part;
            parts.push_back(part);
        } else {
            break;
        }
    }
    if (sum == target) {
        for (int part : parts) {
            cout << part << " ";
        }
        cout << endl;
        return;
    }
    if (sum > target) {
        cout << "error" << endl;
        return;
    }
    vector<int> bestParts;
    int bestSum = sum;
    for (int i = 0; i < parts.size(); i++) {
        int newSum = sum;
        int newPart = 0;
        for (int j = i; j < parts.size(); j++) {
            newPart = newPart * 10 + parts[j];
            newSum += newPart;
            if (newSum > target) {
                break;
            }
            if (newSum > bestSum) {
                bestSum = newSum;
                bestParts = vector<int>(parts.begin() + i, parts.begin() + j + 1);
            }
        }
    }
    if (bestSum == sum) {
        for (int part : parts) {
            cout << part << " ";
        }
        cout << endl;
        return;
    }
    if (bestParts.size() > 1) {
        cout << "rejected" << endl;
        return;
    }
    for (int part : bestParts) {
        cout << part << " ";
    }
    cout << endl;
}

int main() {
    int target, num;
    while (cin >> target >> num) {
        if (target == 0 && num == 0) {
            break;
        }
        shredder(target, to_string(num));
    }
    return 0;
}
