def shredder(target, num):
    if target == num:
        return f"{num}"

    parts = []
    sum_parts = 0
    i = 0
    while i < len(num):
        max_digit = 0
        max_index = -1
        for j in range(i, len(num)):
            digit = int(num[j])
            if digit > max_digit:
                max_digit = digit
                max_index = j
        parts.append(num[i:max_index+1])
        sum_parts += int(num[i:max_index+1])
        i = max_index + 1

    if sum_parts > target:
        return "error"

    min_diff = float('inf')
    min_diff_parts = []
    for i in range(len(parts)):
        for j in range(i+1, len(parts)+1):
            sum_parts = 0
            for k in range(i, j):
                sum_parts += int(parts[k])
            diff = target - sum_parts
            if diff < min_diff:
                min_diff = diff
                min_diff_parts = parts[i:j]
            elif diff == min_diff:
                min_diff_parts = "rejected"

    if min_diff_parts == "rejected":
        return "rejected"

    result = " ".join(parts)
    result += f" {sum_parts}"
    for part in min_diff_parts:
        result += f" {part}"
    return result

