#include <stdio.h>
#include <stdlib.h>

#define MAX_N 1000
#define MAX_P 300

int n, p1, p2;
int x[MAX_N], y[MAX_N], a[MAX_N];
int divine[MAX_P + MAX_P];

int main() {
    while (scanf("%d %d %d", &n, &p1, &p2) == 3) {
        if (n == 0 && p1 == 0 && p2 == 0) break;

        for (int i = 0; i < n; i++) {
            scanf("%d %d %s", &x[i], &y[i], a[i]);
            a[i] == 'y' ? a[i] = 1 : a[i] = 0;
        }

        int i = 0;
        while (i < n && p1 + p2 > 1) {
            int j = i;
            while (j < n && p1 + p2 > 1) {
                if (x[j] == y[i] && a[j] == 1) {
                    p1--;
                    divine[x[i]] = 1;
                } else if (x[j] == y[i] && a[j] == 0) {
                    p2--;
                    divine[x[i]] = 0;
                } else if (y[j] == y[i] && a[j] == 1) {
                    p1--;
                    divine[x[j]] = 1;
                } else if (y[j] == y[i] && a[j] == 0) {
                    p2--;
                    divine[x[j]] = 0;
                }
                j++;
            }
            i++;
        }

        int count = 0;
        for (int i = 1; i <= p1 + p2; i++) {
            if (divine[i]) count++;
        }

        if (count == p1) {
            for (int i = 1; i <= p1 + p2; i++) {
                if (divine[i]) printf("%d\n", i);
            }
            printf("end\n");
        } else {
            printf("no\n");
        }
    }

    return 0;
}
