#include <iostream>
#include <vector>
#include <set>
#include <string>

using namespace std;

int main() {
    int n, p1, p2;
    while (cin >> n >> p1 >> p2) {
        if (n == 0 && p1 == 0 && p2 == 0) break;

        vector<pair<int, int>> graph(p1 + p2);
        for (int i = 0; i < n; ++i) {
            int x, y;
            string a;
            cin >> x >> y >> a;
            --x; --y;
            if (a == "yes") {
                graph[x].first = y;
                graph[y].second = x;
            } else {
                graph[x].second = y;
                graph[y].first = x;
            }
        }

        set<int> divine;
        for (int i = 0; i < p1 + p2; ++i) {
            if (graph[i].first == -1) {
                divine.insert(i);
            }
        }

        for (int i = 0; i < p1 + p2; ++i) {
            if (graph[i].first != -1) {
                int x = graph[i].first;
                if (divine.find(x) != divine.end()) {
                    divine.insert(i);
                }
            }
        }

        if (divine.size() == p1) {
            for (int x : divine) {
                cout << x + 1 << endl;
            }
            cout << "end" << endl;
        } else {
            cout << "no" << endl;
        }
    }

    return 0;
}
