from collections import defaultdict

def true_liars():
    while True:
        n, p1, p2 = map(int, input().split())
        if n == 0 and p1 == 0 and p2 == 0:
            break

        graph = defaultdict(list)
        for _ in range(n):
            x, y, a = input().split()
            x, y = int(x), int(y)
            if a == 'yes':
                graph[x].append(y)
                graph[y].append(x)

        visited = set()
        divine = set()
        stack = [1]
        while stack:
            node = stack.pop()
            if node not in visited:
                visited.add(node)
                if node <= p1:
                    divine.add(node)
                for neighbor in graph[node]:
                    if neighbor not in visited:
                        stack.append(neighbor)

        if len(divine) == p1:
            for num in sorted(divine):
                print(num)
            print('end')
        else:
            print('no')

