#include <iostream>
#include <cmath>
#include <vector>

struct Point {
    double x, y, z;
};

struct Disc {
    Point center;
    double radius;
};

bool isPointInsideDisc(const Point& point, const Disc& disc) {
    return pow(point.x - disc.center.x, 2) + pow(point.y - disc.center.y, 2) + pow(point.z - disc.center.z, 2) <= pow(disc.radius, 2);
}

bool isDiscVisible(const Disc& disc, const std::vector<Disc>& discs) {
    for (const auto& otherDisc : discs) {
        if (otherDisc.radius > disc.radius && isPointInsideDisc(disc.center, otherDisc)) {
            return false;
        }
    }
    return true;
}

int main() {
    int n;
    while (std::cin >> n) {
        if (n == 0) {
            break;
        }
        std::vector<Disc> discs(n);
        for (int i = 0; i < n; ++i) {
            std::cin >> discs[i].center.x >> discs[i].center.y >> discs[i].center.z >> discs[i].radius;
        }
        int visibleCount = 0;
        for (const auto& disc : discs) {
            if (isDiscVisible(disc, discs)) {
                ++visibleCount;
            }
        }
        std::cout << visibleCount << std::endl;
    }
    return 0;
}
