#include <stdio.h>
#include <string.h>
#include <ctype.h>

#define MAXLEN 26

char messengers[] = "JCEAMP";
int messenger_op[] = {
    1, // rotate left
    -1, // rotate right
    0, // swap halves
    -1, // reverse
    1, // increment digits
    -1 // decrement digits
};

void apply_op(char *str, int op) {
    int len = strlen(str);
    if (op == 0) {
        int mid = len / 2;
        char temp = str[mid];
        for (int i = mid; i < len - 1; i++) {
            str[i] = str[i + 1];
        }
        str[len - 1] = temp;
    } else if (op == 1) {
        for (int i = 0; i < len; i++) {
            if (isdigit(str[i])) {
                str[i] = (str[i] - '0' + 1) % 10 + '0';
            }
        }
    } else if (op == -1) {
        for (int i = 0; i < len; i++) {
            if (isdigit(str[i])) {
                str[i] = (str[i] - '0' - 1 + 10) % 10 + '0';
            }
        }
    } else {
        int i = 0;
        int j = len - 1;
        while (i < j) {
            char temp = str[i];
            str[i] = str[j];
            str[j] = temp;
            i++;
            j--;
        }
        if (op == -1) {
            int k = 0;
            while (k < len && str[k] == ' ') {
                k++;
            }
            int l = len - 1;
            while (l >= 0 && str[l] == ' ') {
                l--;
            }
            if (k < l) {
                for (int i = k; i <= l; i++) {
                    str[i] = str[i + 1];
                }
                len--;
            }
        }
    }
}

int main() {
    int n;
    scanf("%d", &n);
    for (int i = 0; i < n; i++) {
        int len;
        scanf("%d", &len);
        char order[7];
        scanf("%s", order);
        char message[100];
        scanf("%s", message);
        int m = strlen(order);
        for (int j = m - 1; j >= 0; j--) {
            int k = order[j] - 'A';
            apply_op(message, messenger_op[k]);
        }
        printf("%s\n", message);
    }
    return 0;
}
