#include <iostream>
#include <map>
#include <vector>
#include <algorithm>

int main() {
    std::map<int, int> freq;
    int num;

    while (std::cin >> num) {
        if (freq.find(num) != freq.end()) {
            freq[num]++;
        } else {
            freq[num] = 1;
        }
    }

    int maxFreq = 0;
    for (const auto& pair : freq) {
        if (pair.second > maxFreq) {
            maxFreq = pair.second;
        }
    }

    std::vector<int> modeValues;
    for (const auto& pair : freq) {
        if (pair.second == maxFreq) {
            modeValues.push_back(pair.first);
        }
    }

    std::sort(modeValues.begin(), modeValues.end());

    for (const auto& value : modeValues) {
        std::cout << value << " ";
    }

    return 0;
}
