import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int t = scanner.nextInt();
        scanner.nextLine();

        for (int i = 0; i < t; i++) {
            String order = scanner.nextLine();
            String message = scanner.nextLine();

            StringBuilder originalMessage = new StringBuilder(message);

            for (int j = order.length() - 1; j >= 0; j--) {
                char messenger = order.charAt(j);

                switch (messenger) {
                    case 'J':
                        originalMessage = new StringBuilder(originalMessage.toString().substring(1) + originalMessage.toString().charAt(0));
                        break;
                    case 'C':
                        originalMessage = new StringBuilder(originalMessage.substring(originalMessage.length() - 1) + originalMessage.substring(0, originalMessage.length() - 1));
                        break;
                    case 'E':
                        int length = originalMessage.length();
                        if (length % 2 == 0) {
                            originalMessage = new StringBuilder(originalMessage.substring(length / 2, length) + originalMessage.substring(0, length / 2));
                        } else {
                            originalMessage = new StringBuilder(originalMessage.substring(length / 2 + 1) + originalMessage.substring(0, length / 2 + 1) + originalMessage.substring(length / 2 + 1, length));
                        }
                        break;
                    case 'A':
                        originalMessage = originalMessage.reverse();
                        break;
                    case 'P':
                        for (int k = 0; k < originalMessage.length(); k++) {
                            if (Character.isDigit(originalMessage.charAt(k))) {
                                originalMessage.setCharAt(k, (char) ((originalMessage.charAt(k) - '0' + 1) % 10 + '0'));
                            }
                        }
                        break;
                    case 'M':
                        for (int k = 0; k < originalMessage.length(); k++) {
                            if (Character.isDigit(originalMessage.charAt(k))) {
                                originalMessage.setCharAt(k, (char) ((originalMessage.charAt(k) - '0' - 1 + 10) % 10 + '0'));
                            }
                        }
                        break;
                }
            }

            System.out.println(originalMessage.toString());
        }
    }
}
