def rotate_left(s):
    return s[1:] + s[0]

def rotate_right(s):
    return s[-1] + s[:-1]

def swap_halves(s):
    mid = len(s) // 2
    if len(s) % 2 == 0:
        return s[mid:] + s[:mid]
    else:
        return s[mid+1:] + s[:mid+1] + s[mid]

def reverse(s):
    return s[::-1]

def increment_digits(s):
    return ''.join(str((int(c) + 1) % 10) if c.isdigit() else c for c in s)

def decrement_digits(s):
    return ''.join(str((int(c) - 1) % 10) if c.isdigit() else c for c in s)

def infer_message(order, message):
    for op in reversed(order):
        if op == 'J':
            message = rotate_left(message)
        elif op == 'C':
            message = rotate_right(message)
        elif op == 'E':
            message = swap_halves(message)
        elif op == 'A':
            message = reverse(message)
        elif op == 'P':
            message = increment_digits(message)
        elif op == 'M':
            message = decrement_digits(message)
    return message

def main():
    n = int(input())
    for _ in range(n):
        order = input()
        message = input()
        print(infer_message(order, message))

if __name__ == "__main__":
    main()
