    for (i = 1; i <= MAX_SQ; i++) {
        for (j = 1; j <= MAX_SQ; j++) {
            n = i * i + j * j;
            if (n <= MAX_N) {
                count[n]++;
            }
        }
    }

    for (i = 1; i <= MAX_SQ; i++) {
        for (j = 1; j <= MAX_SQ; j++) {
            for (k = 1; k <= MAX_SQ; k++) {
                n = i * i + j * j + k * k;
