#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int sign(int x) {
    return (x > 0) - (x < 0);
}

int cross(int x1, int y1, int x2, int y2) {
    return x1 * y2 - x2 * y1;
}

int main() {
    int m, x, y;
    vector<int> xs, ys;
    int area = 0;

    while (cin >> m) {
        if (m == 0) break;

        for (int i = 0; i < m; i++) {
            cin >> x >> y;
            xs.push_back(x);
            ys.push_back(y);
        }

        for (int i = 0; i < m; i++) {
            int x1 = xs[i], y1 = ys[i];
            int x2 = xs[(i + 1) % m], y2 = ys[(i + 1) % m];

            for (int j = i + 2; j < m; j++) {
                int x3 = xs[j], y3 = ys[j];
                int x4 = xs[(j + 1) % m], y4 = ys[(j + 1) % m];

                area += abs(cross(x2 - x1, y2 - y1, x4 - x3, y4 - y3));
            }
        }

        cout << area / 2 << endl;
        area = 0;
        xs.clear();
        ys.clear();
    }

    return 0;
}
