import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.ArrayList;
import java.util.List;

public class Main {
    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String line;
        int count = 0;
        while ((line = br.readLine()) != null) {
            if (line.equals("0")) {
                break;
            }
            int m = Integer.parseInt(line);
            List<int[]> points = new ArrayList<>();
            for (int i = 0; i < m; i++) {
                String[] parts = br.readLine().split(" ");
                points.add(new int[]{Integer.parseInt(parts[0]), Integer.parseInt(parts[1])});
            }
            int area = countArea(points);
            System.out.println(area);
            count++;
        }
    }

    private static int countArea(List<int[]> points) {
        int area = 0;
        for (int i = 0; i < points.size(); i++) {
            int[] p1 = points.get(i);
            int[] p2 = points.get((i + 1) % points.size());
            for (int x = Math.min(p1[0], p2[0]); x <= Math.max(p1[0], p2[0]); x++) {
                for (int y = Math.min(p1[1], p2[1]); y <= Math.max(p1[1], p2[1]); y++) {
                    if (isInside(x, y, p1, p2)) {
                        area++;
                    }
                }
            }
        }
        return area;
    }

    private static boolean isInside(int x, int y, int[] p1, int[] p2) {
        if (p1[0] == p2[0]) {
            return (x == p1[0] && y >= Math.min(p1[1], p2[1]) && y <= Math.max(p1[1], p2[1]));
        } else if (p1[1] == p2[1]) {
            return (y == p1[1] && x >= Math.min(p1[0], p2[0]) && x <= Math.max(p1[0], p2[0]));
        } else {
            return false;
        }
    }
}
