import java.util.*;
import java.io.*;

public class Main {
    static int[][] cloud = {{0, 0}, {0, 1}, {0, 2}, {0, 3}, {1, 0}, {1, 1}, {1, 2}, {1, 3}, {2, 0}, {2, 1}, {2, 2}, {2, 3}, {3, 0}, {3, 1}, {3, 2}, {3, 3}};
    static int[][] dir = {{-1, 0}, {0, -1}, {1, 0}, {0, 1}};
    static int[][] dp;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        int T = Integer.parseInt(br.readLine());
        while (T-- > 0) {
            int n = Integer.parseInt(br.readLine());
            dp = new int[16][n + 1];
            for (int i = 0; i < 16; i++) {
                for (int j = 0; j <= n; j++) {
                    dp[i][j] = -1;
                }
            }
            boolean ok = true;
            for (int i = 0; i < n; i++) {
                StringTokenizer st = new StringTokenizer(br.readLine());
                int[] a = new int[16];
                for (int j = 0; j < 16; j++) {
                    a[j] = Integer.parseInt(st.nextToken());
                }
                for (int j = 0; j < 16; j++) {
                    if (a[j] == 1) {
                        ok = false;
                    }
                }
                if (!ok) {
                    pw.println(0);
                    break;
                }
                for (int j = 0; j < 16; j++) {
                    if (a[j] == 1) {
                        for (int k = 0; k < 16; k++) {
                            if (dp[k][i] == -1) {
                                dp[k][i] = 0;
                            }
                        }
                    }
                }
                for (int j = 0; j < 16; j++) {
                    if (dp[j][i] != -1) {
                        for (int k = 0; k < 4; k++) {
                            int x = j / 4 + dir[k][0];
                            int y = j % 4 + dir[k][1];
                            if (x >= 0 && x < 4 && y >= 0 && y < 4) {
                                int p = x * 4 + y;
                                if (dp[p][i] == -1) {
                                    dp[p][i] = dp[j][i] + 1;
                                } else {
                                    dp[p][i] = Math.min(dp[p][i], dp[j][i] + 1);
                                }
                            }
                        }
                    }
                }
                if (dp[6][i] == -1 || dp[7][i] == -1 || dp[10][i] == -1 || dp[11][i] == -1) {
                    ok = false;
                }
                if (i == n - 1) {
                    pw.println(ok ? 1 : 0);
                }
            }
        }
        pw.close();
    }
}
