def is_valid_move(x, y):
    return 0 <= x < 4 and 0 <= y < 4

def can_satisfy_everyone(days, schedules):
    cloud_x, cloud_y = 1, 1
    for day in range(days):
        for i in range(4):
            for j in range(4):
                if (i, j) == (cloud_x, cloud_y):
                    schedules[day][i*4 + j] = 1
                elif (i, j) in [(cloud_x-1, cloud_y-1), (cloud_x-1, cloud_y), (cloud_x-1, cloud_y+1),
                                (cloud_x, cloud_y-1), (cloud_x, cloud_y+1),
                                (cloud_x+1, cloud_y-1), (cloud_x+1, cloud_y), (cloud_x+1, cloud_y+1)]:
                    schedules[day][i*4 + j] = 0
        if day < days - 1:
            dx, dy = 0, 0
            while True:
                if not is_valid_move(cloud_x + dx, cloud_y + dy):
                    break
                if day + 1 + abs(cloud_x + dx - cloud_x) + abs(cloud_y + dy - cloud_y) >= days:
                    break
                if (cloud_x + dx, cloud_y + dy) in [(cloud_x-1, cloud_y-1), (cloud_x-1, cloud_y), (cloud_x-1, cloud_y+1),
                                                        (cloud_x, cloud_y-1), (cloud_x, cloud_y+1),
                                                        (cloud_x+1, cloud_y-1), (cloud_x+1, cloud_y), (cloud_x+1, cloud_y+1)]:
                    break
                cloud_x += dx
                cloud_y += dy
                if day + 1 + abs(cloud_x - cloud_x) + abs(cloud_y - cloud_y) >= days:
                    break
                dx, dy = 0, 0
                if day + 1 + abs(cloud_x - 1 - cloud_x) + abs(cloud_y - cloud_y) < days:
                    dx = -1
                if day + 1 + abs(cloud_x + 1 - cloud_x) + abs(cloud_y - cloud_y) < days:
                    dx = 1
                if day + 1 + abs(cloud_x - cloud_x) + abs(cloud_y - 1 - cloud_y) < days:
                    dy = -1
                if day + 1 + abs(cloud_x - cloud_x) + abs(cloud_y + 1 - cloud_y) < days:
                    dy = 1
                if dx == 0 and dy == 0:
                    break
            if (cloud_x, cloud_y) == (1, 1):
                return False
    return True

def main():
    while True:
        days = int(input())
        if days == 0:
            break
        schedules = [list(map(int, input().split())) for _ in range(days)]
        print(int(can_satisfy_everyone(days, schedules)))

if __name__ == "__main__":
    main()
