#include <iostream>
#include <string>
#include <map>
#include <regex>

using namespace std;

int main() {
    map<string, int> atomicTable;
    string line;
    bool firstPart = true;

    while (getline(cin, line)) {
        if (line == "END OF FIRST PART") {
            firstPart = false;
            continue;
        }

        if (firstPart) {
            regex pattern("([A-Z][a-z]?\\s?)(\\d+)");
            smatch match;
            if (regex_search(line, match, pattern)) {
                atomicTable[match[1].str()] = stoi(match[2].str());
            }
        } else {
            if (line == "0") break;

            regex pattern("(\\([A-Z][a-z]?\\))?(\\d*)");
            smatch match;
            if (regex_search(line, match, pattern)) {
                string formula = line;
                formula.erase(0, formula.find_first_not_of("()"));
                formula.erase(formula.find_last_not_of("()") + 1);

                int weight = 0;
                regex symbolPattern("[A-Z][a-z]?");
                auto symbolsBegin = sregex_iterator(formula.begin(), formula.end(), symbolPattern);
                auto symbolsEnd = sregex_iterator();

                for (sregex_iterator i = symbolsBegin; i != symbolsEnd; ++i) {
                    smatch symbol = *i;
                    string symbolStr = symbol.str();

                    if (atomicTable.find(symbolStr) != atomicTable.end()) {
                        int count = 1;
                        if (match[2].matched) {
                            regex countPattern("\\d+");
                            smatch countMatch;
                            if (regex_search(match[2].str(), countMatch, countPattern)) {
                                count = stoi(countMatch.str());
                            }
                        }

                        weight += atomicTable[symbolStr] * count;
                    } else {
                        weight = -1; // UNKNOWN
                        break;
                    }
                }

                if (weight != -1) {
                    cout << weight << endl;
                } else {
                    cout << "UNKNOWN" << endl;
                }
            }
        }
    }

    return 0;
}
