import java.util.HashMap;
import java.util.Map;
import java.util.Scanner;

public class MolecularFormula {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        Map<String, Integer> atomicTable = new HashMap<>();

        while (true) {
            String line = scanner.nextLine();
            if (line.equals("END OF FIRST PART")) {
                break;
            }
            String[] parts = line.split("\\s+");
            if (parts.length == 2) {
                atomicTable.put(parts[0], Integer.parseInt(parts[1]));
            }
        }

        while (true) {
            String line = scanner.nextLine();
            if (line.equals("0")) {
                break;
            }
            String molecularFormula = line;
            Map<String, Integer> weights = new HashMap<>();
            int weight = 0;

            for (int i = 0; i < molecularFormula.length(); i++) {
                if (Character.isLetter(molecularFormula.charAt(i))) {
                    String symbol = "";
                    while (i < molecularFormula.length() && Character.isLetter(molecularFormula.charAt(i))) {
                        symbol += molecularFormula.charAt(i);
                        i++;
                    }
                    if (i < molecularFormula.length() && Character.isDigit(molecularFormula.charAt(i))) {
                        int count = 0;
                        while (i < molecularFormula.length() && Character.isDigit(molecularFormula.charAt(i))) {
                            count = count * 10 + molecularFormula.charAt(i) - '0';
                            i++;
                        }
                        if (count >= 2 && count <= 99) {
                            if (atomicTable.containsKey(symbol)) {
                                weights.put(symbol, count);
                            }
                        } else {
                            if (atomicTable.containsKey(symbol)) {
                                weights.put(symbol, 1);
                            }
                        }
                    } else {
                        if (atomicTable.containsKey(symbol)) {
                            weights.put(symbol, 1);
                        }
                    }
                }
            }

            boolean unknown = false;
            for (String symbol : weights.keySet()) {
                if (!atomicTable.containsKey(symbol)) {
                    unknown = true;
                    break;
                }
            }

            if (unknown) {
                System.out.println("UNKNOWN");
            } else {
                for (String symbol : weights.keySet()) {
                    weight += atomicTable.get(symbol) * weights.get(symbol);
                }
                System.out.println(weight);
            }
        }
    }
}
