import re

def calculate_molecular_weight(formula, atomic_weights):
    stack = []
    i = 0
    while i < len(formula):
        if formula[i].isalpha():
            j = i + 1
            while j < len(formula) and formula[j].isalpha():
                j += 1
            symbol = formula[i:j]
            i = j
            if symbol in atomic_weights:
                stack.append(atomic_weights[symbol])
            else:
                return "UNKNOWN"
        elif formula[i].isdigit():
            j = i
            while j < len(formula) and formula[j].isdigit():
                j += 1
            count = int(formula[i:j])
            i = j
            if stack and stack[-1] == count:
                stack.pop()
                stack.append(stack.pop() * count)
            else:
                stack.append(count)
        elif formula[i] == '(':
            j = i + 1
            while j < len(formula) and formula[j] != ')':
                j += 1
            if j == len(formula):
                return "UNKNOWN"
            subformula = formula[i+1:j]
            i = j + 1
            if subformula in atomic_weights:
                stack.append(atomic_weights[subformula])
            else:
                return "UNKNOWN"
        elif formula[i] == ')':
            return "UNKNOWN"
        else:
            return "UNKNOWN"
    return sum(stack)

def main():
    atomic_weights = {}
    while True:
        line = input()
        if line == "END OF FIRST PART":
            break
        parts = line.split()
        atomic_weights[parts[0]] = int(parts[1])
    while True:
        line = input()
        if line == "0":
            break
        print(calculate_molecular_weight(line, atomic_weights))

if __name__ == "__main__":
    main()
