#include <iostream>
#include <vector>
#include <algorithm>

struct Application {
    int start, end, price;
};

bool compareApplications(const Application& a, const Application& b) {
    return a.price > b.price;
}

int main() {
    int n;
    while (std::cin >> n && n != 0) {
        std::vector<Application> applications(n);
        for (int i = 0; i < n; ++i) {
            std::cin >> applications[i].start >> applications[i].end >> applications[i].price;
        }

        std::sort(applications.begin(), applications.end(), compareApplications);

        int maxIncome = 0;
        int currentRoom = 0;
        int currentIncome = 0;
        int currentEnd = 0;

        for (const auto& app : applications) {
            if (app.start > currentEnd) {
                currentRoom = 0;
                currentIncome = 0;
                currentEnd = app.end;
            }

            if (currentRoom == 0 || app.end > currentEnd) {
                currentIncome += app.price;
                currentEnd = app.end;
                currentRoom = 1;
            }
        }

        std::cout << currentIncome << std::endl;
    }

    return 0;
}
