import java.util.Arrays;
import java.util.Scanner;

class Application implements Comparable<Application> {
    int start;
    int end;
    int price;

    public Application(int start, int end, int price) {
        this.start = start;
        this.end = end;
        this.price = price;
    }

    @Override
    public int compareTo(Application other) {
        return Integer.compare(this.end, other.end);
    }
}

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (scanner.hasNextInt()) {
            int n = scanner.nextInt();
            Application[] applications = new Application[n];
            for (int i = 0; i < n; i++) {
                int start = scanner.nextInt();
                int end = scanner.nextInt();
                int price = scanner.nextInt();
                applications[i] = new Application(start, end, price);
            }
            if (n == 0) {
                break;
            }
            Arrays.sort(applications);
            int[] dp = new int[365];
            for (Application application : applications) {
                int max = 0;
                for (int i = application.start; i <= application.end; i++) {
                    max = Math.max(max, dp[i]);
                }
                dp[application.end] = Math.max(dp[application.end], max + application.price);
            }
            int max = 0;
            for (int i = 0; i < 365; i++) {
                max = Math.max(max, dp[i]);
            }
            System.out.println(max);
        }
    }
}
