import sys

class Application:
    def __init__(self, i, j, w):
        self.i = i
        self.j = j
        self.w = w

def solve(apps):
    apps.sort(key=lambda x: x.w, reverse=True)
    rooms = [0] * 366
    total_income = 0

    for app in apps:
        for day in range(app.i, app.j + 1):
            if rooms[day] == 0:
                rooms[day] = app.w
                total_income += app.w
                break

    return total_income

def main():
    input = sys.stdin.readline
    output = sys.stdout.write

    t = 1
    for _ in range(t):
        n = int(input())
        apps = []
        for _ in range(n):
            i, j, w = map(int, input().split())
            apps.append(Application(i, j, w))

        output(str(solve(apps)) + '\n')

if __name__ == "__main__":
    main()
