#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define MAXN 100
#define EPS 1e-5

typedef struct {
    double x, y;
} Point;

typedef struct {
    Point p1, p2;
} Line;

int ccw(Point p, Point q, Point r) {
    return (q.x - p.x) * (r.y - q.y) - (q.y - p.y) * (r.x - q.x);
}

int intersect(Line l1, Line l2, Point *p) {
    double x1 = l1.p1.x, y1 = l1.p1.y, x2 = l1.p2.x, y2 = l1.p2.y;
    double x3 = l2.p1.x, y3 = l2.p1.y, x4 = l2.p2.x, y4 = l2.p2.y;

    double denom = (x1 - x2) * (y3 - y4) - (y1 - y2) * (x3 - x4);
    if (abs(denom) < EPS) return 0;

    double t = ((x1 - x3) * (y3 - y4) - (y1 - y3) * (x3 - x4)) / denom;
    double u = -((x1 - x2) * (y1 - y3) - (y1 - y2) * (x1 - x3)) / denom;

    if (t > 0 && t < 1 && u > 0 && u < 1) {
        p->x = x1 + t * (x2 - x1);
        p->y = y1 + t * (y2 - y1);
        return 1;
    }

    return 0;
}

int isConvex(Point *p, int n) {
    int i;
    for (i = 0; i < n; i++) {
        Point q = p[i];
        Point r = p[(i + 1) % n];
        Point s = p[(i + 2) % n];
        if (ccw(q, r, s) > 0) return 0;
    }
    return 1;
}

int isInside(Point *p, int n) {
    int i;
    for (i = 0; i < n; i++) {
        Point q = p[i];
        Point r = p[(i + 1) % n];
        Point s = p[(i + 2) % n];
        if (ccw(q, r, s) < 0) return 0;
    }
    return 1;
}

int isMonstertap(int n, Line *l) {
    int i;
    Point p;
    for (i = 0; i < n; i++) {
        Line l1 = l[i];
        Line l2 = l[(i + 1) % n];
        if (intersect(l1, l2, &p)) {
            if (isInside(l, n)) return 0;
        }
    }
    return isConvex(l, n);
}

int main() {
    int n, i;
    Line l[MAXN];
    while (scanf("%d", &n) == 1) {
        if (n == 0) break;
        for (i = 0; i < n; i++) {
            scanf("%lf%lf%lf%lf", &l[i].p1.x, &l[i].p1.y, &l[i].p2.x, &l[i].p2.y);
        }
        if (isMonstertap(n, l)) printf("yes\n");
        else printf("no\n");
    }
    return 0;
}
