import java.util.*;
import java.io.*;

public class Main {
    static class Point {
        double x, y;

        public Point(double x, double y) {
            this.x = x;
            this.y = y;
        }
    }

    static class Line {
        Point p1, p2;

        public Line(Point p1, Point p2) {
            this.p1 = p1;
            this.p2 = p2;
        }
    }

    static double crossProduct(Point p1, Point p2, Point p3) {
        return (p2.x - p1.x) * (p3.y - p1.y) - (p2.y - p1.y) * (p3.x - p1.x);
    }

    static boolean isInside(Point p, List<Line> lines) {
        int n = lines.size();
        int count = 0;
        for (int i = 0; i < n; i++) {
            Point p1 = lines.get(i).p1;
            Point p2 = lines.get(i).p2;
            if (Math.signum(crossProduct(p, p1, p2)) == 0) {
                if (Math.hypot(p.x - p1.x, p.y - p1.y) < 1e-5) {
                    return true;
                }
                if (Math.hypot(p.x - p2.x, p.y - p2.y) < 1e-5) {
                    return true;
                }
            }
            if (crossProduct(p, p1, p2) > 0) {
                count++;
            }
        }
        return count % 2 == 1;
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        int T = Integer.parseInt(br.readLine());
        while (T-- > 0) {
            int n = Integer.parseInt(br.readLine());
            List<Line> lines = new ArrayList<>();
            while (n-- > 0) {
                StringTokenizer st = new StringTokenizer(br.readLine());
                Point p1 = new Point(Double.parseDouble(st.nextToken()), Double.parseDouble(st.nextToken()));
                Point p2 = new Point(Double.parseDouble(st.nextToken()), Double.parseDouble(st.nextToken()));
                lines.add(new Line(p1, p2));
            }
            if (isInside(new Point(0, 0), lines)) {
                System.out.println("yes");
            } else {
                System.out.println("no");
            }
        }
    }
}
