#include <stdio.h>
#include <string.h>
#include <ctype.h>

#define MAX 1000
#define MAX_WORD 32

int main() {
    char text[MAX];
    char word[MAX_WORD];
    int max_freq = 0;
    int max_len = 0;
    int freq[MAX_WORD];
    int len[MAX_WORD];
    int max_word[MAX_WORD];
    int max_word_len = 0;

    fgets(text, MAX, stdin);
    text[strcspn(text, "\n")] = 0;

    for (int i = 0; i < strlen(text); i++) {
        if (isspace(text[i])) {
            word[strcspn(text, " ")] = 0;
            for (int j = 0; j < strlen(word); j++) {
                if (isalpha(word[j])) {
                    len[strcspn(word, " ")]++;
                    if (len[strcspn(word, " ")] > max_len) {
                        max_len = len[strcspn(word, " ")];
                        strcpy(max_word, word);
                    }
                }
            }
            memset(len, 0, sizeof(len));
            memset(word, 0, sizeof(word));
        } else {
            word[strcspn(text, " ")] = tolower(text[i]);
        }
    }

    for (int i = 0; i < strlen(word); i++) {
        if (isalpha(word[i])) {
            freq[strcspn(word, " ")]++;
            if (freq[strcspn(word, " ")] > max_freq) {
                max_freq = freq[strcspn(word, " ")];
                strcpy(max_word, word);
            }
        }
    }

    printf("%s %s\n", max_word, max_word_len == max_len ? max_word : max_word_len);

    return 0;
}
