#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int a, b, d;
    while (cin >> a >> b >> d) {
        if (a == 0 && b == 0 && d == 0) break;

        vector<pair<int, int>> solutions;
        for (int x = 0; x <= d / a; x++) {
            int y = (d - x * a) / b;
            if ((d - x * a) % b == 0) {
                solutions.push_back(make_pair(x, y));
            }
        }

        int min_weights = INT_MAX;
        for (auto& solution : solutions) {
            int total_weights = solution.first + solution.second;
            if (total_weights < min_weights) {
                min_weights = total_weights;
            }
        }

        for (auto& solution : solutions) {
            if (solution.first + solution.second == min_weights) {
                cout << solution.first << " " << solution.second << endl;
                break;
            }
        }
    }

    return 0;
}
