#include <iostream>
#include <vector>
#include <string>
#include <sstream>

using namespace std;

struct Move {
    int x, y, z;
};

struct Sequence {
    int x, y, z, length;
    bool isBlack;
};

bool isSequence(const vector<Move>& moves, int x, int y, int z, int length, bool isBlack) {
    int count = 0;
    for (int i = 0; i < moves.size(); i++) {
        if (moves[i].x == x && moves[i].y == y && moves[i].z == z + count) {
            count++;
            if (count == length) {
                return true;
            }
        }
    }
    return false;
}

bool checkSequence(const vector<Move>& moves, int n, int m, bool isBlack) {
    for (int x = 1; x <= n; x++) {
        for (int y = 1; y <= n; y++) {
            for (int z = 1; z <= n; z++) {
                if (isSequence(moves, x, y, z, m, isBlack)) {
                    return true;
                }
            }
        }
    }
    return false;
}

void solve() {
    int n, m, p;
    cin >> n >> m >> p;

    vector<Move> moves;
    for (int i = 0; i < p; i++) {
        int x, y;
        cin >> x >> y;
        moves.push_back({x, y, 1});
    }

    bool isBlackTurn = true;
    int count = 0;
    while (true) {
        if (checkSequence(moves, n, m, isBlackTurn)) {
            cout << (isBlackTurn ? "Black" : "White") << " " << count << endl;
            return;
        }
        if (moves.size() == p) {
            cout << "Draw" << endl;
            return;
        }
        moves.push_back({moves.back().x, moves.back().y, moves.back().z + 1});
        isBlackTurn = !isBlackTurn;
        count++;
    }
}

int main() {
    int t;
    cin >> t;
    while (t--) {
        solve();
    }
    return 0;
}
