#include <iostream>
#include <string>
#include <map>
#include <algorithm>

int main() {
    std::string text;
    std::getline(std::cin, text);

    std::map<std::string, int> wordCount;
    std::map<std::string, int> maxWordLength;

    size_t pos = 0;
    while ((pos = text.find(' ')) != std::string::npos) {
        std::string word = text.substr(0, pos);
        ++wordCount[word];
        maxWordLength[word] = std::max(maxWordLength[word], static_cast<int>(word.length()));
        text.erase(0, pos + 1);
    }

    std::string maxWord;
    int maxCount = 0;
    for (const auto& pair : wordCount) {
        if (pair.second > maxCount) {
            maxCount = pair.second;
            maxWord = pair.first;
        }
    }

    std::string maxWordLengthWord;
    for (const auto& pair : maxWordLength) {
        if (pair.second > maxWordLength[""]) {
            maxWordLength[""] = pair.second;
            maxWordLengthWord = pair.first;
        }
    }

    std::cout << maxWord << " " << maxWordLengthWord << std::endl;

    return 0;
}
