import os

def simplify_path(path):
    stack = []
    for segment in path.split('/'):
        if segment == '' or segment == '.':
            continue
        elif segment == '..':
            if stack:
                stack.pop()
        else:
            stack.append(segment)
    return '/'.join(stack)

def find_path(path, paths):
    simplified = simplify_path(path)
    for p in paths:
        if simplify_path(p) == simplified:
            return True
    return False

def main():
    while True:
        try:
            n, m = map(int, input().split())
            paths = []
            for _ in range(n):
                paths.append(simplify_path(input()))
            for _ in range(m):
                path1 = simplify_path(input())
                path2 = simplify_path(input())
                if find_path(path1, paths) and find_path(path2, paths) and path1 == path2:
                    print('yes')
                elif not find_path(path1, paths) or not find_path(path2, paths):
                    print('not found')
                else:
                    print('no')
        except ValueError:
            break

if __name__ == '__main__':
    main()
