#include <stdio.h>
#include <string.h>
#include <stdbool.h>

#define MAX_NAME_LENGTH 16
#define MAX_NAMES 200

bool isConfusing(const char *name1, const char *name2, int d) {
    int len1 = strlen(name1);
    int len2 = strlen(name2);
    int minLen = (len1 < len2) ? len1 : len2;

    for (int i = 0; i < minLen; i++) {
        if (name1[i] != name2[i]) {
            return true;
        }
    }

    if (len1 == len2) {
        return false;
    }

    int diff = (len1 < len2) ? len1 : len2;
    for (int i = 0; i < diff; i++) {
        if (name1[i] != name2[i]) {
            return true;
        }
    }

    return false;
}

void swap(char *a, char *b) {
    char temp = *a;
    *a = *b;
    *b = temp;
}

void replace(char *a, char *b) {
    *a = *b;
}

void delete(char *a) {
    for (int i = 0; i < strlen(a); i++) {
        a[i] = a[i + 1];
    }
}

void insert(char *a, char c) {
    int len = strlen(a);
    for (int i = len; i > 0; i--) {
        a[i] = a[i - 1];
    }
    a[0] = c;
}

void printPair(const char *name1, const char *name2) {
    if (strcmp(name1, name2) <= 0) {
        printf("%s,%s\n", name1, name2);
    } else {
        printf("%s,%s\n", name2, name1);
    }
}

int main() {
    int n, d;
    while (scanf("%d", &n) == 1) {
        if (n == 0) {
            break;
        }
        scanf("%d", &d);
        char names[MAX_NAMES][MAX_NAME_LENGTH];
        for (int i = 0; i < n; i++) {
            scanf("%s", names[i]);
        }

        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                if (isConfusing(names[i], names[j], d)) {
                    printPair(names[i], names[j]);
                }
            }
        }

        printf("%d\n", n * (n - 1) / 2);
    }

    return 0;
}
