#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

int editDistance(const string& s1, const string& s2) {
    int m = s1.size();
    int n = s2.size();
    vector<vector<int>> dp(m + 1, vector<int>(n + 1, 0));

    for (int i = 0; i <= m; i++) {
        dp[i][0] = i;
    }
    for (int j = 0; j <= n; j++) {
        dp[0][j] = j;
    }

    for (int i = 1; i <= m; i++) {
        for (int j = 1; j <= n; j++) {
            if (s1[i - 1] == s2[j - 1]) {
                dp[i][j] = dp[i - 1][j - 1];
            } else {
                dp[i][j] = 1 + min(min(dp[i - 1][j], dp[i][j - 1]), dp[i - 1][j - 1]);
            }
        }
    }

    return dp[m][n];
}

void solve() {
    int n;
    cin >> n;
    int d;
    cin >> d;
    vector<string> names(n);
    for (int i = 0; i < n; i++) {
        cin >> names[i];
    }

    vector<pair<string, string>> pairs;
    for (int i = 0; i < n; i++) {
        for (int j = i + 1; j < n; j++) {
            if (editDistance(names[i], names[j]) <= d) {
                if (names[i] < names[j]) {
                    pairs.push_back({names[i], names[j]});
                } else {
                    pairs.push_back({names[j], names[i]});
                }
            }
        }
    }

    sort(pairs.begin(), pairs.end());
    for (const auto& pair : pairs) {
        cout << pair.first << "," << pair.second << endl;
    }
    cout << pairs.size() << endl;
}

int main() {
    int t;
    cin >> t;
    while (t--) {
        solve();
        if (cin.peek() == '\n') {
            cin.ignore();
        }
    }
    return 0;
}
