from itertools import permutations

def levenshtein_distance(s1, s2):
    if len(s1) > len(s2):
        s1, s2 = s2, s1

    distances = range(len(s1) + 1)
    for i2, c2 in enumerate(s2):
        distances_ = [i2+1]
        for i1, c1 in enumerate(s1):
            if c1 == c2:
                distances_.append(distances[i1])
            else:
                distances_.append(1 + min((distances[i1], distances[i1 + 1], distances_[-1])))
        distances = distances_
    return distances[-1]

def swap_distance(s1, s2):
    if len(s1) != len(s2):
        return float('inf')
    distance = 0
    for i in range(len(s1)):
        if s1[i] != s2[i]:
            distance += 1
    return distance

def is_confusing(s1, s2, d):
    return levenshtein_distance(s1, s2) <= d or swap_distance(s1, s2) <= d

def main():
    while True:
        n = int(input())
        d = int(input())
        names = []
        for _ in range(n):
            names.append(input())
        confusing_pairs = set()
        for i in range(n):
            for j in range(i + 1, n):
                if is_confusing(names[i], names[j], d):
                    pair = tuple(sorted((names[i], names[j])))
                    confusing_pairs.add(pair)
        print('\n'.join(','.join(pair) for pair in sorted(confusing_pairs)))
        print(len(confusing_pairs))
        if input() == '0':
            break

if __name__ == '__main__':
    main()
