import java.util.HashMap;
import java.util.Map;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        String text = scanner.nextLine();
        scanner.close();

        String[] words = text.split(" ");
        Map<String, Integer> wordCount = new HashMap<>();
        Map<String, Integer> wordLength = new HashMap<>();

        for (String word : words) {
            if (wordCount.containsKey(word)) {
                wordCount.put(word, wordCount.get(word) + 1);
            } else {
                wordCount.put(word, 1);
            }
            wordLength.put(word, word.length());
        }

        int maxCount = 0;
        int maxCountWord = "";
        int maxLength = 0;
        int maxLengthWord = "";

        for (Map.Entry<String, Integer> entry : wordCount.entrySet()) {
            if (entry.getValue() > maxCount) {
                maxCount = entry.getValue();
                maxCountWord = entry.getKey();
            }
        }

        for (Map.Entry<String, Integer> entry : wordLength.entrySet()) {
            if (entry.getValue() > maxLength) {
                maxLength = entry.getValue();
                maxLengthWord = entry.getKey();
            }
        }

        if (wordCount.get(maxCountWord) == wordCount.get(maxLengthWord)) {
            System.out.println(maxLengthWord + " " + maxCountWord);
        } else {
            System.out.println(maxCountWord + " " + maxLengthWord);
        }
    }
}
