from collections import defaultdict
import sys

def dfs(node, color, graph, visited, adj):
    visited[node] = True
    for neighbor in adj[node]:
        if not visited[neighbor]:
            color[neighbor] = color[node]
            dfs(neighbor, color, graph, visited, adj)
        elif color[neighbor] == color[node]:
            return False
    return True

def min_colors(graph):
    n = len(graph)
    color = [0] * n
    visited = [False] * n
    for i in range(n):
        if not visited[i]:
            color[i] = 1
            if not dfs(i, color, graph, visited, graph):
                color[i] += 1
    return max(color)

