#include <iostream>
#include <vector>
#include <algorithm>
#include <cmath>

using namespace std;

struct Sphere {
    int x, y, z, r;
};

bool intersect(const Sphere& a, const Sphere& b) {
    int dx = a.x - b.x;
    int dy = a.y - b.y;
    int dz = a.z - b.z;
    int dr = a.r + b.r;
    return dx * dx + dy * dy + dz * dz <= dr * dr;
}

int getConnectedFigures(const vector<Sphere>& spheres, int z) {
    int n = spheres.size();
    vector<bool> visited(n);
    int count = 0;
    for (int i = 0; i < n; ++i) {
        if (!visited[i]) {
            count++;
            vector<int> stack = {i};
            while (!stack.empty()) {
                int j = stack.back();
                stack.pop_back();
                visited[j] = true;
                for (int k = 0; k < n; ++k) {
                    if (!visited[k] && intersect(spheres[j], spheres[k]) && spheres[k].z <= z + spheres[k].r) {
                        stack.push_back(k);
                    }
                }
            }
        }
    }
    return count;
}

int main() {
    int n;
    while (cin >> n && n != 0) {
        vector<Sphere> spheres(n);
        for (int i = 0; i < n; ++i) {
            cin >> spheres[i].x >> spheres[i].y >> spheres[i].z >> spheres[i].r;
        }
        sort(spheres.begin(), spheres.end(), [](const Sphere& a, const Sphere& b) {
            return a.z < b.z;
        });
        int m = 0;
        int z = 0;
        int prev = 0;
        while (z <= 36000) {
            int count = getConnectedFigures(spheres, z);
            if (count != prev) {
                m++;
                prev = count;
            }
            z += 1;
        }
        cout << m << endl;
        for (int i = m - 1; i >= 0; --i) {
            cout << (prev > getConnectedFigures(spheres, z - 1) ? 1 : 0);
            z--;
        }
        cout << endl;
    }
    return 0;
}
