#include <stdio.h>
#include <math.h>

#define PI 3.14159265358979323846

double calculate_area(double x1, double y1, double x2, double y2) {
    return 0.5 * abs(x1 * y2 - x2 * y1);
}

double calculate_surface_area(double n, double points[n][2]) {
    double total_area = 0;
    for (int i = 0; i < n; i++) {
        double x1 = points[i][0];
        double y1 = points[i][1];
        double x2 = points[(i + 1) % n][0];
        double y2 = points[(i + 1) % n][1];
        total_area += calculate_area(x1, y1, x2, y2);
    }
    return total_area;
}

int main() {
    int n;
    while (scanf("%d", &n) == 1) {
        if (n == 0) break;
        double points[n][2];
        for (int i = 0; i < n; i++) {
            scanf("%lf %lf", &points[i][0], &points[i][1]);
        }
        printf("%.5lf\n", calculate_surface_area(n, points));
        for (int i = 0; i < n; i++) {
            scanf("%lf %lf", &points[i][0], &points[i][1]);
        }
        printf("%.5lf\n", calculate_surface_area(n, points));
    }
    return 0;
}
