#include <iostream>
#include <cmath>
#include <iomanip>

using namespace std;

double calculateSurfaceArea(int n, int points[][2]) {
    double totalSurfaceArea = 0;
    for (int i = 0; i < n; i++) {
        int x1 = points[i][0];
        int y1 = points[i][1];
        int x2 = points[(i + 1) % n][0];
        int y2 = points[(i + 1) % n][1];

        double baseArea = abs(x2 - x1) * abs(y2 - y1);
        double height = max(abs(x1), abs(y1));
        double surfaceArea = 2 * baseArea + 2 * height * abs(x2 - x1);

        totalSurfaceArea += surfaceArea;
    }

    return totalSurfaceArea;
}

int main() {
    int n;
    while (true) {
        cin >> n;
        if (n == 0) break;

        int points[n][2];
        for (int i = 0; i < n; i++) {
            cin >> points[i][0] >> points[i][1];
        }

        double surfaceArea = calculateSurfaceArea(n, points);
        cout << fixed << setprecision(5) << surfaceArea << endl;
    }

    return 0;
}
