import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            int n = scanner.nextInt();
            if (n == 0) {
                break;
            }
            int[][] points = new int[n][2];
            for (int i = 0; i < n; i++) {
                points[i][0] = scanner.nextInt();
                points[i][1] = scanner.nextInt();
            }
            points = addPoint(points, n);
            double area = calculateArea(points, n);
            System.out.printf("%.4f%n", area);
        }
    }

    private static int[][] addPoint(int[][] points, int n) {
        int[][] newPoints = new int[n + 1][2];
        System.arraycopy(points, 0, newPoints, 0, n);
        newPoints[n][0] = points[0][0];
        newPoints[n][1] = points[0][1];
        return newPoints;
    }

    private static double calculateArea(int[][] points, int n) {
        double area = 0;
        for (int i = 0; i < n; i++) {
            int x1 = points[i][0];
            int y1 = points[i][1];
            int x2 = points[(i + 1) % n][0];
            int y2 = points[(i + 1) % n][1];
            area += Math.abs(x1 * y2 - x2 * y1);
        }
        return area / 2;
    }
}
