#include <stdio.h>
#include <stdbool.h>
#include <math.h>

bool is_prime(int n) {
    if (n <= 1) return false;
    if (n <= 3) return true;
    if (n % 2 == 0 || n % 3 == 0) return false;
    int i = 5;
    while (i * i <= n) {
        if (n % i == 0 || n % (i + 2) == 0) return false;
        i += 6;
    }
    return true;
}

int count_representations(int n) {
    int count = 0;
    for (int i = 2; i <= n; i++) {
        if (!is_prime(i)) continue;
        int sum = 0;
        for (int j = i; j <= n; j++) {
            sum += j;
            if (sum == n) count++;
            if (sum > n) break;
        }
    }
    return count;
}

int main() {
    int n;
    while (scanf("%d", &n) == 1) {
        if (n == 0) break;
        printf("%d\n", count_representations(n));
    }
    return 0;
}
