#include <stdio.h>

int main() {
    int n, i, p, q, id, count = 0;
    while (scanf("%d", &n) == 1) {
        if (n == 0) break;
        int max = 0;
        for (int j = 0; j < n; j++) {
            scanf("%d %d %d", &id, &p, &q);
            int sales = p * q;
            if (sales >= 1000000) {
                printf("%d\n", id);
                count++;
                max = id;
            }
        }
        if (count == 0) printf("NA\n");
        else {
            for (int j = 0; j < n; j++) {
                scanf("%d %d %d", &id, &p, &q);
                int sales = p * q;
                if (sales >= 1000000 && id != max) {
                    printf("%d\n", id);
                    count++;
                }
            }
        }
        count = 0;
        printf("\n");
    }
    return 0;
}
