#include <iostream>
#include <queue>
#include <vector>
#include <algorithm>

using namespace std;

struct Book {
    int id;
    int time;
};

int main() {
    int m, c, n;
    while (cin >> m >> c >> n) {
        if (m == 0 && c == 0 && n == 0) break;

        vector<int> k(n);
        vector<vector<int>> b(n);
        for (int i = 0; i < n; i++) {
            cin >> k[i];
            b[i].resize(k[i]);
            for (int j = 0; j < k[i]; j++) {
                cin >> b[i][j];
            }
        }

        priority_queue<Book, vector<Book>, greater<Book>> queue;
        vector<int> d(m, 0);
        int cost = 0;

        for (int i = 0; i < n; i++) {
            for (int j = 0; j < k[i]; j++) {
                int book = b[i][j];
                int time = queue.size() + 1;

                if (d[0] < c) {
                    d[0]++;
                    queue.push({book, time});
                } else {
                    int minTime = queue.top().time;
                    int minIndex = 0;
                    for (int l = 1; l < m; l++) {
                        if (queue[l].time < minTime) {
                            minTime = queue[l].time;
                            minIndex = l;
                        }
                    }

                    if (d[minIndex] < c) {
                        d[minIndex]++;
                        queue.push({book, time});
                    } else {
                        queue.pop();
                        queue.push({book, time});
                    }

                    if (minIndex > 0) {
                        d[minIndex]--;
                        d[0]++;
                        queue.push({queue.top().id, queue.top().time});
                        queue.pop();
                    } else {
                        queue.push({book, time});
                    }
                }

                cost += m + 1 - min(time, m);
            }
        }

        cout << cost << endl;
    }

    return 0;
}
