import java.util.*;
import java.io.*;

public class Main {
    static class Book {
        int id;
        int time;

        public Book(int id, int time) {
            this.id = id;
            this.time = time;
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);

        while (true) {
            StringTokenizer st = new StringTokenizer(br.readLine());
            int m = Integer.parseInt(st.nextToken());
            int c = Integer.parseInt(st.nextToken());
            int n = Integer.parseInt(st.nextToken());

            if (m == 0 && c == 0 && n == 0) break;

            int[][] requests = new int[n][];
            for (int i = 0; i < n; i++) {
                st = new StringTokenizer(br.readLine());
                int k = Integer.parseInt(st.nextToken());
                requests[i] = new int[k];
                for (int j = 0; j < k; j++) {
                    requests[i][j] = Integer.parseInt(st.nextToken());
                }
            }

            int[][] books = new int[m][c];
            int[] time = new int[m];
            int cost = 0;

            for (int i = 0; i < n; i++) {
                for (int j = 0; j < requests[i].length; j++) {
                    int bookId = requests[i][j];
                    boolean found = false;

                    for (int k = 0; k < m; k++) {
                        if (books[k][0] == 0) {
                            books[k][0] = bookId;
                            time[k] = j;
                            found = true;
                            break;
                        } else if (books[k][0] == bookId) {
                            books[k][0] = 0;
                            time[k] = j;
                            found = true;
                            break;
                        }
                    }

                    if (!found) {
                        for (int k = 0; k < m; k++) {
                            if (time[k] < time[0]) {
                                int temp = books[k][0];
                                books[k][0] = bookId;
                                time[k] = j;
                                found = true;
                                break;
                            }
                        }

                        if (!found) {
                            for (int k = 0; k < m; k++) {
                                if (books[k][0] == 0) {
                                    books[k][0] = bookId;
                                    time[k] = j;
                                    found = true;
                                    break;
                                }
                            }

                            if (!found) {
                                cost += m + 1;
                            }
                        }
                    }

                    if (found) {
                        cost += m - k;
                    }
                }
            }

            pw.println(cost);
        }

        pw.close();
    }
}
