#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX 6
#define MAXC 25

typedef struct {
    char color[MAXC];
} Cube;

typedef struct {
    int n;
    Cube cubes[MAX];
} Dataset;

int rotate(Cube c, int i) {
    char temp[MAXC];
    strcpy(temp, c.color[i]);
    for (int j = 0; j < 5; j++) {
        c.color[j] = c.color[(j + i) % 6];
    }
    strcpy(c.color[5], temp);
    return 0;
}

int match(Cube c1, Cube c2) {
    for (int i = 0; i < 6; i++) {
        if (strcmp(c1.color[i], c2.color[i]) != 0) {
            return 0;
        }
    }
    return 1;
}

int isIdentical(Cube c1, Cube c2) {
    for (int i = 0; i < 6; i++) {
        if (match(c1, c2) == 1) {
            return 1;
        }
        rotate(c1, i);
    }
    return 0;
}

int minRepaint(Dataset d) {
    int count = 0;
    for (int i = 0; i < d.n; i++) {
        for (int j = i + 1; j < d.n; j++) {
            if (isIdentical(d.cubes[i], d.cubes[j]) == 0) {
                count++;
            }
        }
    }
    return count;
}

int main() {
    int n;
    Dataset d;
    while (scanf("%d", &n) == 1) {
        if (n == 0) {
            break;
        }
        d.n = n;
        for (int i = 0; i < n; i++) {
            scanf("%s", d.cubes[i].color);
            for (int j = 1; j < 6; j++) {
                scanf("%s", d.cubes[i].color + j * MAXC);
            }
        }
        printf("%d\n", minRepaint(d));
    }
    return 0;
}
