from collections import defaultdict
from itertools import permutations

def rotate(cube):
    return [
        cube[0][3] + cube[0][2] + cube[0][1],
        cube[1][3] + cube[1][2] + cube[1][1],
        cube[2][3] + cube[2][2] + cube[2][1],
        cube[3][3] + cube[3][2] + cube[3][1],
        cube[4][3] + cube[4][2] + cube[4][1],
        cube[5][3] + cube[5][2] + cube[5][1],
    ]

def hash_cube(cube):
    return tuple(sorted(set(rotate(cube) + [cube])))

def min_faces_to_paint(cubes):
    faces_to_paint = float('inf')
    for rot in permutations(range(6)):
        cube_hashes = set()
        for cube in cubes:
            cube_rot = tuple(rotate(cube)[i] for i in rot)
            cube_hashes.add(hash_cube(cube_rot))
        if len(cube_hashes) == 1:
            faces_to_paint = min(faces_to_paint, sum(1 for i in range(6) if cube_hashes.pop() != cube[i]))
    return faces_to_paint

def main():
    while True:
        n = int(input())
        if n == 0:
            break
        cubes = [input().split() for _ in range(n)]
        print(min_faces_to_paint(cubes))

if __name__ == "__main__":
    main()
