#include <iostream>
#include <vector>
#include <string>
#include <queue>
#include <algorithm>

using namespace std;

const int MAX_N = 4;
const int MAX_M = 5;
const int MAX_S = 10;

struct Edge {
    int u, v;
    char d;
};

struct State {
    vector<string> s;
    vector<int> dist;
    vector<int> prev;
    int moves;
};

int main() {
    int T;
    cin >> T;
    while (T--) {
        int n, m;
        cin >> n >> m;
        vector<Edge> edges(m);
        for (int i = 0; i < m; i++) {
            cin >> edges[i].u >> edges[i].d >> edges[i].v;
        }
        vector<string> s(n), t(n);
        for (int i = 0; i < n; i++) {
            cin >> s[i];
        }
        for (int i = 0; i < n; i++) {
            cin >> t[i];
        }
        int moves = bfs(n, m, edges, s, t);
        cout << moves << endl;
    }
    return 0;
}

int bfs(int n, int m, vector<Edge> edges, vector<string> s, vector<string> t) {
    vector<vector<int>> adj(n);
    for (int i = 0; i < m; i++) {
        adj[edges[i].u].push_back(edges[i].v);
        adj[edges[i].v].push_back(edges[i].u);
    }
    vector<int> dist(n, -1);
    vector<int> prev(n, -1);
    queue<int> q;
    for (int i = 0; i < n; i++) {
        if (s[i] == t[i]) {
            dist[i] = 0;
            q.push(i);
        }
    }
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        for (int v : adj[u]) {
            if (dist[v] == -1) {
                dist[v] = dist[u] + 1;
                prev[v] = u;
                q.push(v);
            }
        }
    }
    int moves = 0;
    for (int i = 0; i < n; i++) {
        if (dist[i] != -1) {
            moves = max(moves, dist[i]);
        }
    }
    return moves;
}
