#include <iostream>
#include <vector>
#include <map>
#include <algorithm>

int main() {
    std::string input;
    std::getline(std::cin, input); // ignore the first line

    while (true) {
        int n;
        std::cin >> n;
        if (n == 0) break;

        std::map<int, long long> sales;
        for (int i = 0; i < n; ++i) {
            int id;
            long long p, q;
            std::cin >> id >> p >> q;
            sales[id] += p * q;
        }

        std::vector<int> result;
        for (const auto& pair : sales) {
            if (pair.second >= 1000000) result.push_back(pair.first);
        }

        if (result.empty()) std::cout << "NA" << std::endl;
        else {
            std::sort(result.begin(), result.end());
            for (int id : result) std::cout << id << std::endl;
        }

        std::cout << std::endl;
        std::getline(std::cin, input); // ignore the blank line
    }

    return 0;
}
