from collections import deque

def solve():
    x, y = map(int, input().split())
    edges = {}
    for _ in range(y):
        u, d, v, dd = input().split()
        u, v = int(u), int(v)
        if u not in edges:
            edges[u] = []
        if v not in edges:
            edges[v] = []
        edges[u].append((v, d == 'E'))
        edges[v].append((u, dd == 'E'))
    arrival = []
    departure = []
    for _ in range(x):
        arrival.append(input().strip())
        departure.append(input().strip())
    arrival = [list(s) for s in arrival]
    departure = [list(s) for s in departure]
    queue = deque([(0, 0, 0, 0, 0)])
    visited = set([(0, 0, 0, 0, 0)])
    while queue:
        u, v, i, j, moves = queue.popleft()
        if u == v and i == len(arrival[u]) and j == len(departure[u]):
            print(moves)
            return
        for w, d in edges[u]:
            if (w, v, i, j, moves + 1) not in visited:
                visited.add((w, v, i, j, moves + 1))
                queue.append((w, v, i, j, moves + 1))
        if i < len(arrival[u]):
            if arrival[u][i] != departure[u][j]:
                for w, d in edges[u]:
                    if (w, v, i + 1, j, moves + 1) not in visited:
                        visited.add((w, v, i + 1, j, moves + 1))
                        queue.append((w, v, i + 1, j, moves + 1))
        if j < len(departure[u]):
            if arrival[u][i] == departure[u][j]:
                for w, d in edges[u]:
                    if (w, v, i, j + 1, moves + 1) not in visited:
                        visited.add((w, v, i, j + 1, moves + 1))
                        queue.append((w, v, i, j + 1, moves + 1))

solve()
