#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define MAX_STONES 6

double max(double a, double b) {
    return (a > b) ? a : b;
}

double min(double a, double b) {
    return (a < b) ? a : b;
}

double mobile(double r, int s, int w[], int i, int j) {
    if (i > j) return 0;
    if (i == j) return w[i] * (i + 1);
    double a = mobile(r, s, w, i, j - 1);
    double b = mobile(r, s, w, i + 1, j);
    if (a == 0 || b == 0) return max(a, b);
    double c = (w[i] * (j - i) * a) / b;
    return c + b;
}

int main() {
    int t;
    scanf("%d", &t);
    while (t--) {
        double r;
        int s;
        int w[MAX_STONES];
        scanf("%lf %d", &r, &s);
        for (int i = 0; i < s; i++) {
            scanf("%d", &w[i]);
        }
        double result = mobile(r, s, w, 0, s - 1);
        if (result >= r) result = -1;
        printf("%.10lf\n", result);
    }
    return 0;
}
