#include <iostream>
#include <vector>
#include <algorithm>
#include <cmath>

using namespace std;

double f(vector<int> &a, int i, int j) {
    if (i == j) return 1;
    double res = 0;
    for (int k = i; k < j; k++) {
        double x = f(a, i, k) + f(a, k + 1, j);
        res = max(res, x);
    }
    return res;
}

double g(vector<int> &a, int i, int j, double r) {
    if (i == j) return 0;
    double res = 0;
    for (int k = i; k < j; k++) {
        double x = f(a, i, k) + f(a, k + 1, j);
        res = max(res, x * (a[k] / (a[i] * (k - i + 1) + a[j] * (j - k))));
    }
    return res + r;
}

int main() {
    int t;
    cin >> t;
    while (t--) {
        double r;
        int s;
        cin >> r >> s;
        vector<int> a(s);
        for (int i = 0; i < s; i++) {
            cin >> a[i];
        }
        sort(a.begin(), a.end());
        double l = 0, h = r;
        while (h - l > 1e-8) {
            double m = (l + h) / 2;
            if (g(a, 0, s - 1, m) <= r) {
                l = m;
            } else {
                h = m;
            }
        }
        if (g(a, 0, s - 1, l) > r) {
            cout << -1 << endl;
        } else {
            cout << g(a, 0, s - 1, l) << endl;
        }
    }
    return 0;
}
