import java.util.*;
import java.io.*;

public class Main {
    static long mod = (long) 1e9 + 7;
    static long inf = (long) 1e18;
    static int N = (int) 1e6 + 5;
    static double eps = 1e-9;

    static double f(int[] a, int n, double r) {
        if (n == 1) return a[0];
        double ans = 0;
        for (int i = 1; i < n; i++) {
            for (int j = i + 1; j <= n; j++) {
                double x = f(Arrays.copyOfRange(a, 0, i), i, r);
                double y = f(Arrays.copyOfRange(a, i, j), j - i, r);
                double z = f(Arrays.copyOfRange(a, j, n), n - j, r);
                double sum = a[i - 1] * x + a[j - 1] * y + a[n - 1] * z;
                double diff = x + y + z;
                if (Math.abs(sum * diff - a[n - 1] * diff * diff) < eps) {
                    ans = Math.max(ans, x + y + z);
                }
            }
        }
        if (ans == 0) return -1;
        return ans;
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        int t = Integer.parseInt(br.readLine());
        while (t-- > 0) {
            StringTokenizer st = new StringTokenizer(br.readLine());
            double r = Double.parseDouble(st.nextToken());
            int n = Integer.parseInt(st.nextToken());
            int[] a = new int[n];
            st = new StringTokenizer(br.readLine());
            for (int i = 0; i < n; i++) {
                a[i] = Integer.parseInt(st.nextToken());
            }
            double ans = f(a, n, r);
            if (ans == -1) System.out.println(-1);
            else System.out.printf("%.10f\n", ans);
        }
    }
}
